document.addEventListener("DOMContentLoaded",()=>{let e=document.querySelector(".burger-menu"),t=document.querySelector(".menu"),s=document.querySelector(".account_buttons");e.addEventListener("click",()=>{e.classList.toggle("active"),t.classList.remove("active","reverse"),s.classList.remove("active","reverse"),setTimeout(()=>{e.classList.contains("active")?(t.classList.add("active"),s.classList.add("active")):(t.classList.add("reverse"),s.classList.add("reverse"))},0)})});